/**
 * –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –∏–∫–æ–Ω–∫–∏ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
 * @param {string} emoji - –ó–Ω–∞—á–µ–Ω–∏–µ –∏–∫–æ–Ω–∫–∏ (–º–æ–∂–µ—Ç –±—ã—Ç—å —ç–º–æ–¥–∑–∏ –¥–ª—è –æ–±—Ä–∞—Ç–Ω–æ–π —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç–∏ –∏–ª–∏ –∫–ª—é—á –∏–∑ react-icons)
 * @param {string} size - –†–∞–∑–º–µ—Ä –∏–∫–æ–Ω–∫–∏ (sm, md, lg)
 */
export function CategoryIcon({ emoji, size = "md" }) {
  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —è–≤–ª—è–µ—Ç—Å—è –ª–∏ –∑–Ω–∞—á–µ–Ω–∏–µ –∫–ª—é—á–æ–º –¥–ª—è react-icons
  const ReactIconComponent = reactIconMap[emoji];

  if (ReactIconComponent) {
    return (
      <IconWrapper $size={size}>
        <ReactIconComponent />
      </IconWrapper>
    );
  }

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —è–≤–ª—è–µ—Ç—Å—è –ª–∏ –∑–Ω–∞—á–µ–Ω–∏–µ —Å—Ç–∞—Ä—ã–º —ç–º–æ–¥–∑–∏
  const legacyIcon = legacyIconMap[emoji];

  if (legacyIcon) {
    return <IconWrapper $size={size}>{legacyIcon}</IconWrapper>;
  }

  // Fallback: –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —ç–º–æ–¥–∑–∏, –µ—Å–ª–∏ –∏–∫–æ–Ω–∫–∞ –Ω–µ –Ω–∞–π–¥–µ–Ω–∞
  return <FallbackEmoji $size={size}>{emoji}</FallbackEmoji>;
}

const IconWrapper = styled.div`
  width: ${(props) => {
    switch (props.$size) {
      case "sm":
        return "24px";
      case "lg":
        return "48px";
      default:
        return "32px";
    }
  }};
  height: ${(props) => {
    switch (props.$size) {
      case "sm":
        return "24px";
      case "lg":
        return "48px";
      default:
        return "32px";
    }
  }};
  color: ${theme.colors.accent.primary};
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;

  svg {
    width: 100%;
    height: 100%;
  }
`;

const FallbackEmoji = styled.span`
  font-size: ${(props) => {
    switch (props.$size) {
      case "sm":
        return "20px";
      case "lg":
        return "40px";
      default:
        return "28px";
    }
  }};
  line-height: 1;
`;
